class TrieNode {
    TrieNode[] links;
    int temp;

    TrieNode() {
        links = new TrieNode[26];
        temp = Integer.MAX_VALUE;
    }

    TrieNode get(int idx){
        return links[idx];
    }

    void put(int idx,TrieNode node){
        links[idx]=node;
    }
}

class Trie {
    TrieNode root;

    Trie() {
        root = new TrieNode();
    }

    void insert(String[] words, int[] costs) {
        for (int i = 0; i < words.length; i++) {
            TrieNode node = root;
            for (char ch : words[i].toCharArray()) {
                int idx = ch - 'a';
                if (node.get(idx) == null) {
                    node.put(idx, new TrieNode());
                }
                node = node.get(idx);
            }
            node.temp = Math.min(node.temp, costs[i]);  
        }
    }

    TrieNode extractTheRoot() {
        return root;
    }
}

class Solution {
    public int minimumCost(String targetStr, String[] w, int[] c) {
        int n = targetStr.length();
        int[] dpRaj = new int[n + 1];
        for(int i=0; i<dpRaj.length; i++){
            dpRaj[i]=Integer.MAX_VALUE;
        }
        dpRaj[0] = 0; 

        Trie trie = new Trie();
        trie.insert(w, c);
        TrieNode root = trie.extractTheRoot();

        for (int i=0; i<n; i++) {
            if (dpRaj[i] == Integer.MAX_VALUE){
                continue;
            }
            TrieNode node = root;
            for (int j=i; j<n; j++) {
                char ch=targetStr.charAt(j);
                int idx=ch-'a';
                node = node.get(idx);
                if (node==null){
                    break;
                }
                if (node.temp==Integer.MAX_VALUE) {
                    continue;
                }else{
                    dpRaj[j + 1] = Math.min(dpRaj[j + 1], dpRaj[i] + node.temp);
                }
            }
        }
        if(dpRaj[n]==Integer.MAX_VALUE){
            return -1;
        }
        return dpRaj[n];
    }
}
