#include <vector>
#include <string>
#include <unordered_map>
#include <algorithm>
#include <climits>

using namespace std;

class TrieNode {
public:
    unordered_map<char, TrieNode*> children;
    int cost;

    TrieNode() : cost(INT_MAX) {}
};

class Solution {
public:
    TrieNode* root;

    void insert(string word, int cost) {
        TrieNode* node = root;
        for (char c : word) {
            if (!node->children.count(c)) {
                node->children[c] = new TrieNode();
            }
            node = node->children[c];
        }
        node->cost = min(node->cost, cost);
    }

    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.size();

        // Build the trie
        root = new TrieNode();
        for (int i = 0; i < words.size(); ++i) {
            insert(words[i], costs[i]);
        }

        // DP array
        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0;

        // Fill the DP array
        for (int i = 0; i < n; ++i) {
            if (dp[i] == INT_MAX) continue;  // Skip unreachable states

            TrieNode* node = root;
            for (int j = i; j < n; ++j) {
                if (!node->children.count(target[j])) break;
                node = node->children[target[j]];
                if (node->cost != INT_MAX) {
                    dp[j + 1] = min(dp[j + 1], dp[i] + node->cost);
                }
            }
        }

        return dp[n] != INT_MAX ? dp[n] : -1;
    }
};
