class TrieNode {
    TrieNode[] children;
    List<int[]> wordInfo; // stores pairs of (length of the word, cost of the word)

    TrieNode() {
        children = new TrieNode[26];
        wordInfo = new ArrayList<>();
    }
}

class Trie {
    TrieNode root;

    Trie() {
        root = new TrieNode();
    }

    void insert(String word, int cost) {
        TrieNode node = root;
        for (char c : word.toCharArray()) {
            int index = c - 'a';
            if (node.children[index] == null) {
                node.children[index] = new TrieNode();
            }
            node = node.children[index];
        }
        node.wordInfo.add(new int[]{word.length(), cost});
    }

    List<int[]> search(String target, int start) {
        List<int[]> result = new ArrayList<>();
        TrieNode node = root;
        for (int i = start; i < target.length(); i++) {
            int index = target.charAt(i) - 'a';
            if (node.children[index] == null) break;
            node = node.children[index];
            result.addAll(node.wordInfo);
        }
        return result;
    }
}


class Solution {
    public int minimumCost(String target, String[] words, int[] costs) {
        int n = target.length();
        int[] dp = new int[n + 1];
        Arrays.fill(dp, Integer.MAX_VALUE);
        dp[0] = 0;

        Trie trie = new Trie();
        for (int i = 0; i < words.length; i++) {
            trie.insert(words[i], costs[i]);
        }

        for (int i = 0; i < n; i++) {
            if (dp[i] == Integer.MAX_VALUE) continue;
            List<int[]> matches = trie.search(target, i);
            for (int[] match : matches) {
                int len = match[0];
                int cost = match[1];
                if (i + len <= n) {
                    dp[i + len] = Math.min(dp[i + len], dp[i] + cost);
                }
            }
        }

        return dp[n] == Integer.MAX_VALUE ? -1 : dp[n];
    }
}
