class Solution {
        class TrieNode {
        Map<Character, TrieNode> children = new HashMap<>();
        int cost = Integer.MAX_VALUE;
    }

    class Trie {
        TrieNode root = new TrieNode();

        void insert(String word, int cost) {
            TrieNode node = root;
            for (char c : word.toCharArray()) {
                node.children.putIfAbsent(c, new TrieNode());
                node = node.children.get(c);
            }
            node.cost = Math.min(node.cost, cost); // Store the minimum cost to form this word
        }

        // Finds all possible end positions and their costs starting from start position in target
        void search(String target, int start, int[] dp) {
            TrieNode node = root;
            for (int i = start; i < target.length(); i++) {
                if (!node.children.containsKey(target.charAt(i))) break;
                node = node.children.get(target.charAt(i));
                if (node.cost != Integer.MAX_VALUE) {
                    dp[i + 1] = Math.min(dp[i + 1], dp[start] + node.cost);
                }
            }
        }
    }

    public int minimumCost(String target, String[] words, int[] costs) {
                int n = target.length();
        int[] dp = new int[n + 1];
        Arrays.fill(dp, Integer.MAX_VALUE);
        dp[0] = 0;

        Trie trie = new Trie();
        for (int i = 0; i < words.length; i++) {
            trie.insert(words[i], costs[i]);
        }

        for (int i = 0; i < n; i++) {
            if (dp[i] == Integer.MAX_VALUE) continue;
            trie.search(target, i, dp);
        }

        return dp[n] == Integer.MAX_VALUE ? -1 : dp[n];
    }
}
