class TrieNode {
public:
    unordered_map<char, TrieNode*> children;
    vector<pair<int, int>> costs; // (word length, cost)
    TrieNode() {}
};

class Trie {
public:
    TrieNode* root;
    Trie() {
        root = new TrieNode();
    }

    void insert(const string& word, int cost) {
        TrieNode* node = root;
        for (char c : word) {
            if (!node->children.count(c)) {
                node->children[c] = new TrieNode();
            }
            node = node->children[c];
        }
        node->costs.push_back({word.length(), cost});
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.length();
        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0; // Cost to create an empty string is 0

        Trie trie;
        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], costs[i]);
        }

        for (int i = 0; i < n; ++i) {
            if (dp[i] == INT_MAX) continue; // Skip unreachable positions
            TrieNode* node = trie.root;
            for (int j = i; j < n; ++j) {
                if (!node->children.count(target[j])) break;
                node = node->children[target[j]];
                for (auto& cost : node->costs) {
                    dp[j + 1] = min(dp[j + 1], dp[i] + cost.second);
                }
            }
        }

        return dp[n] == INT_MAX ? -1 : dp[n];
    }
};
