const int inf = 2e9;
struct node {
    node* nxt[26] = {NULL};
    int cst = inf;
};

class Solution {
public:
    int minimumCost(string t, vector<string>& w, vector<int>& costs) {
        node* root = new node();
        for (int i = 0; i < w.size(); i++) {
            node* cur = root;
            for (int j = 0; j < w[i].size(); j++) {
                if (!cur -> nxt[w[i][j] - 'a']) cur -> nxt[w[i][j] - 'a'] = new node();
                cur = cur -> nxt[w[i][j] - 'a'];
            }
            cur -> cst = min(cur -> cst, costs[i]);
        }

        int n = t.size();

        vector <int> dp (n + 1, inf);
        dp[0] = 0;
        for (int i = 0; i < n; i++) {
            if (dp[i] != inf) {
                node* cur = root;
                for (int j = i; j < n; j++) {
                    if (!cur -> nxt[t[j] - 'a']) break;
                    cur = cur -> nxt[t[j] - 'a'];
                    dp[j + 1] = min(dp[j + 1], dp[i] + cur -> cst);
                }
            }
        }
        if (dp[n] == inf) return -1;
        return dp[n];
    }
};
