import java.util.Arrays;
import java.util.HashMap;
import java.util.Map;

class Solution {
    class TrieNode {
        Map<Character, TrieNode> children;
        int cost;

        TrieNode() {
            children = new HashMap<>();
            cost = Integer.MAX_VALUE;
        }
    }

    public int minimumCost(String target, String[] words, int[] costs) {
        int n = target.length();
        int[] dp = new int[n + 1];
        Arrays.fill(dp, Integer.MAX_VALUE);
        dp[0] = 0;

        TrieNode root = buildTrie(words, costs);

        for (int i = 0; i < n; i++) {
            if (dp[i] == Integer.MAX_VALUE) continue;
            TrieNode node = root;
            for (int j = i; j < n; j++) {
                node = node.children.get(target.charAt(j));
                if (node == null) break;
                if (node.cost != Integer.MAX_VALUE) {
                    dp[j + 1] = Math.min(dp[j + 1], dp[i] + node.cost);
                }
            }
        }

        return dp[n] == Integer.MAX_VALUE ? -1 : dp[n];
    }

    private TrieNode buildTrie(String[] words, int[] costs) {
        TrieNode root = new TrieNode();
        for (int i = 0; i < words.length; i++) {
            TrieNode node = root;
            for (char c : words[i].toCharArray()) {
                node.children.putIfAbsent(c, new TrieNode());
                node = node.children.get(c);
            }
            node.cost = Math.min(node.cost, costs[i]);
        }
        return root;
    }

    public static void main(String[] args) {
        Solution sol = new Solution();
        String target = "r";
        String[] words = {"r", "r", "r", "r"};
        int[] costs = {1, 6, 3, 3};
        System.out.println(sol.minimumCost(target, words, costs));  // Expected output: 1
    }
}
