class TrieNode {
public:
    unordered_map<char, TrieNode*> children;
    vector<int> indices;
};

class Trie {
public:
    TrieNode* root;

    Trie() {
        root = new TrieNode();
    }

    void insert(const string& word, int index) {
        TrieNode* node = root;
        for (char c : word) {
            if (node->children.find(c) == node->children.end()) {
                node->children[c] = new TrieNode();
            }
            node = node->children[c];
        }
        node->indices.push_back(index);
    }

    vector<int> search(const string& prefix) {
        TrieNode* node = root;
        for (char c : prefix) {
            if (node->children.find(c) == node->children.end()) {
                return {};
            }
            node = node->children[c];
        }
        return node->indices;
    }
};

class Solution {
public:

    int minimumCost(string target, vector<string>& words, vector<int>& costs) {

        int n = target.size();
        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0; // The cost to form an empty string is 0

        Trie trie;
        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], i);
        }

        for (int i = 0; i < n; ++i) {
            if (dp[i] == INT_MAX) continue; // If current prefix is not possible, skip

            TrieNode* node = trie.root;
            for (int j = i; j < n; ++j) {
                if (node->children.find(target[j]) == node->children.end()) break;
                node = node->children[target[j]];
                for (int idx : node->indices) {
                    dp[j + 1] = min(dp[j + 1], dp[i] + costs[idx]);
                }
            }
        }

        return dp[n] == INT_MAX ? -1 : dp[n];
    }
};
