import java.util.*;

class Solution {
    class TrieNode {
        Map<Character, TrieNode> children;
        int cost;

        TrieNode() {
            children = new HashMap<>();
            cost = Integer.MAX_VALUE;  // Initialize with a large value
        }
    }

    class Trie {
        TrieNode root;

        Trie() {
            root = new TrieNode();
        }

        void insert(String word, int cost) {
            TrieNode node = root;
            for (char c : word.toCharArray()) {
                node.children.putIfAbsent(c, new TrieNode());
                node = node.children.get(c);
            }
            node.cost = Math.min(node.cost, cost);  // Store the minimum cost
        }

        int getCost(String word) {
            TrieNode node = root;
            for (char c : word.toCharArray()) {
                if (!node.children.containsKey(c)) {
                    return Integer.MAX_VALUE;
                }
                node = node.children.get(c);
            }
            return node.cost;
        }
    }

    public int minimumCost(String target, String[] words, int[] costs) {
        Trie trie = new Trie();
        for (int i = 0; i < words.length; i++) {
            trie.insert(words[i], costs[i]);
        }

        int n = target.length();
        int[] dp = new int[n + 1];
        Arrays.fill(dp, Integer.MAX_VALUE);
        dp[0] = 0;  // Cost to construct an empty string is 0

        for (int i = 0; i < n; i++) {
            if (dp[i] == Integer.MAX_VALUE) continue;

            TrieNode node = trie.root;
            for (int j = i; j < n; j++) {
                char c = target.charAt(j);
                if (!node.children.containsKey(c)) break;
                node = node.children.get(c);
                if (node.cost != Integer.MAX_VALUE) {
                    dp[j + 1] = Math.min(dp[j + 1], dp[i] + node.cost);
                }
            }
        }

        return dp[n] == Integer.MAX_VALUE ? -1 : dp[n];
    }
}
