import java.util.*;

class TrieNode {
    Map<Character, TrieNode> children = new HashMap<>();
    int cost = Integer.MAX_VALUE;
}

class Solution {
    public int minimumCost(String target, String[] words, int[] costs) {
        TrieNode root = new TrieNode();

        // Build the Trie
        for (int i = 0; i < words.length; i++) {
            TrieNode node = root;
            for (char c : words[i].toCharArray()) {
                node.children.putIfAbsent(c, new TrieNode());
                node = node.children.get(c);
            }
            node.cost = Math.min(node.cost, costs[i]);
        }

        // dp[i] represents the minimum cost to form target.substring(0, i)
        int[] dp = new int[target.length() + 1];
        Arrays.fill(dp, Integer.MAX_VALUE);
        dp[0] = 0;

        // Build dp array
        for (int i = 0; i < target.length(); i++) {
            if (dp[i] == Integer.MAX_VALUE) continue;

            TrieNode node = root;
            for (int j = i; j < target.length(); j++) {
                char c = target.charAt(j);
                if (!node.children.containsKey(c)) break;
                node = node.children.get(c);
                if (node.cost != Integer.MAX_VALUE) {
                    dp[j + 1] = Math.min(dp[j + 1], dp[i] + node.cost);
                }
            }
        }

        return dp[target.length()] == Integer.MAX_VALUE ? -1 : dp[target.length()];
    }
}
