class TrieNode:
    def __init__(self):
        self.children = {}
        self.cost = float('inf')
        self.end = False

class Trie:
    def __init__(self):
        self.root = TrieNode()

    def insert(self, word, cost):
        node = self.root
        for char in word:
            if char not in node.children:
                node.children[char] = TrieNode()
            node = node.children[char]
        node.end = True
        node.cost = cost

class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        trie = Trie()
        wordCost = defaultdict(lambda: float('inf'))
        for word, cost in zip(words, costs):
            wordCost[word] = min(wordCost[word], cost)

        for word, cost in wordCost.items():
            trie.insert(word, cost)

        n = len(target)
        dp = [float('inf')] * (n + 1)
        dp[0] = 0
        for i in range(n):
            if dp[i] == float('inf'):
                continue

            node = trie.root
            j = i
            while j < n and target[j] in node.children:
                node = node.children[target[j]]
                if node.end:
                    dp[j + 1] = min(dp[j + 1], dp[i] + node.cost)
                j += 1

        return dp[n] if dp[n] != float('inf') else -1
