from typing import List

class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        n = len(target)
        m = len(words)

        # Create a dictionary to store each word with its minimum cost
        wc = {}
        for i, w in enumerate(words):
            wc[w] = min(wc.get(w, float('inf')), costs[i])

        # Initialize dp array with infinity
        dp = [float('inf')] * (n + 1)
        dp[0] = 0

        # Fill dp array
        for i in range(1, n + 1):
            for w in wc:
                if i >= len(w) and target[i-len(w):i] == w:
                    dp[i] = min(dp[i], dp[i-len(w)] + wc[w])

        # Return minimum cost to form target or -1 if not possible
        return dp[n] if dp[n] != float('inf') else -1
