class TrieNode {
    TrieNode[] children;
    int minCost;
    public TrieNode() {
        children = new TrieNode[26];
        minCost = Integer.MAX_VALUE;
    }
}

class Solution {
    public int minimumCost(String target, String[] words, int[] costs) {
        TrieNode root = new TrieNode();
        for (int i = 0; i < words.length; i++) {
            insert(root, words[i], costs[i]);
        }

        int n = target.length();
        int[] memo = new int[n];
        Arrays.fill(memo, -1);

        int result = dfs(target, root, 0, memo, n);
        return result == Integer.MAX_VALUE ? -1 : result;
    }

    private void insert(TrieNode root, String word, int cost) {
        TrieNode node = root;
        for (char c : word.toCharArray()) {
            int index = c - 'a';
            if (node.children[index] == null) {
                node.children[index] = new TrieNode();
            }
            node = node.children[index];
        }
        node.minCost = Math.min(node.minCost, cost);
    }

    private int dfs(String target, TrieNode root, int start, int[] memo, int n) {
        if (start == n) {
            return 0;
        }
        if (memo[start] != -1) {
            return memo[start];
        }

        TrieNode node = root;
        int minCost = Integer.MAX_VALUE;
        for (int end = start; end < n; end++) {
            int index = target.charAt(end) - 'a';
            if (node.children[index] == null) {
                break;
            }
            node = node.children[index];
            if (node.minCost != Integer.MAX_VALUE) {
                int nextCost = dfs(target, root, end + 1, memo, n);
                if (nextCost != Integer.MAX_VALUE) {
                    minCost = Math.min(minCost, node.minCost + nextCost);
                }
            }
        }

        memo[start] = minCost;
        return minCost;
    }
}
