class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:

        tt = {}
        mx = max(costs)
        def add(w, c):
            r = tt
            for x in w:
                if x not in r:
                    r[x] = {}
                r = r[x]
            r['$'] = min(r.get('$', mx), c)

        for w, c in zip(words, costs):
            add(w, c)

        n = len(target)

        @cache
        def sol(i):
            if i == n: 
                return 0
            r = tt
            res = -1
            for j in range(i, n):
                x = target[j]
                if x not in r:
                    return res
                r = r[x]
                if '$' in r and sol(j+1)>=0:
                    res1 = r['$'] + sol(j+1)
                    res = min(res, res1) if res!=-1 else res1
            return res

        return sol(0)
