struct TrieNode
{
    unordered_map<char, TrieNode *> cd;
    vector<pair<int, int>> id;
};

class Solution
{
public:
    int minimumCost(string target, vector<string> &words, vector<int> &costs)
    {
        int n = target.size();
        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0;
        vector<string> v1 = words;
        vector<int> v2 = costs;

            TrieNode *root = new TrieNode();
        for (int i = 0; i < v1.size(); ++i)
        {
            TrieNode *pipe = root;
            for (char c : v1[i])
            {
                if (pipe->cd.find(c) == pipe->cd.end())
                {
                    pipe->cd[c] = new TrieNode();
                }
                pipe = pipe->cd[c];
            }
            pipe->id.emplace_back(i, v2[i]);
        }
        if(false){
            vector<int> x;
            for(int i=1;i<100;i++){

                x.push_back(i);
            }
            for(auto it:x){
                cout<<it<<" ";
            }
            cout<<endl;
        }


        for (int i = 0; i < n; ++i)
        {
            if (dp[i] == INT_MAX)
                continue;

            TrieNode *pipe = root;
            for (int j = i; j < n; ++j)
            {
                char c = target[j];
                if (pipe->cd.find(c) == pipe->cd.end())
                    break;
                pipe = pipe->cd[c];
                for (auto &[jd, ap] : pipe->id)
                {
                    int sz = v1[jd].size();
                    if (i + sz <= n)
                    {
                        dp[i + sz] = min(dp[i + sz], dp[i] + ap);
                    }
                }
            }
        }
        if(false){
            vector<int> x;
            for(int i=1;i<100;i++){

                x.push_back(i);
            }
            for(auto it:x){
                cout<<it<<" ";
            }
            cout<<endl;
        }

        if(dp[n]==INT_MAX){
            return -1;
        }
        return dp[n];


    }
};
