from collections import defaultdict

class TrieNode:
    def __init__(self):
        self.children = {}
        self.cost = float('inf')

class Trie:
    def __init__(self):
        self.root = TrieNode()

    def insert(self, word: str, cost: int):
        node = self.root
        for char in word:
            if char not in node.children:
                node.children[char] = TrieNode()
            node = node.children[char]
        node.cost = min(node.cost, cost)

    def get_min_costs(self, target: str, start: int):
        node = self.root
        costs = []
        for i in range(start, len(target)):
            if target[i] not in node.children:
                break
            node = node.children[target[i]]
            if node.cost < float('inf'):
                costs.append((i + 1, node.cost))
        return costs

class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        n = len(target)
        dp = [float('inf')] * (n + 1)
        dp[0] = 0

        # Build a Trie with the words and their costs
        trie = Trie()
        for word, cost in zip(words, costs):
            trie.insert(word, cost)

        # Dynamic programming using Trie to get minimum costs
        for i in range(n):
            if dp[i] == float('inf'):
                continue
            min_costs = trie.get_min_costs(target, i)
            for end, cost in min_costs:
                dp[end] = min(dp[end], dp[i] + cost)

        return dp[n] if dp[n] != float('inf') else -1
