#define ll long long
const int N= 5e4+10;
ll dp[N];

struct Node{
    Node* links[26];
    bool flag= false;
    ll cost= INT_MAX;

    bool containsKey(char c){
        return (links[c-'a'] != NULL);
    }

    void put(char c, Node* ref){
        links[c-'a'] = ref;
    }

    Node* get(char c){
        return links[c-'a'];
    }
};

class Trie{
    public:
    Node * root;
    Trie(){
        root = new Node();
    }

  void Insert(string word, ll Cost){
    Node* tmp = root;
    for (int i = 0; i <word.length(); i++){
      if(!tmp->containsKey(word[i])){
          tmp->put(word[i],new Node());
      }
      tmp = tmp->get(word[i]);
    }
    if(tmp->flag){
       tmp->cost = min(tmp->cost,Cost); 
    }
      else{
        tmp->flag = true;
        tmp->cost = Cost;
      }
    }

};


class Solution {
public:
    string s;

    ll func(int i, Trie &t){
       if(i>=s.length()) return 0;
       if(dp[i] != -1) return dp[i];
       Node * tmp = t.root;
       int j=i;
       ll ans = INT_MAX;

       while(j<s.length()){

          if(!tmp->containsKey(s[j])) break;
          tmp = tmp->get(s[j]);
          if(tmp->flag){
             ans = min(ans , tmp->cost + func(j+1,t));
          }
          j++;    
       }

       return dp[i]= ans;
    }


    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
         memset(dp,-1,sizeof(dp));
            Trie t;
           s = target;
           for(int i=0; i<words.size(); i++){
              t.Insert(words[i],costs[i]);
           }

