//                   if(!tmp->containsKey(s[j])) break;
//                   tmp = tmp->get(s[j]);
//                   if(tmp->flag){
//                       cout<<j<<"\n";
//                   }
//                }


           ll ans = func(0,t);
           if(ans>=INT_MAX) return -1;
           return ans;  
    }
};
