class TrieNode {
public:
    unordered_map<char, TrieNode*> children;
    vector<pair<string, int>> wordsWithCost; 
    TrieNode() {}
};
class Trie {
public:
    TrieNode* root;
    Trie() { root = new TrieNode(); }

    void insert(string& word, int cost) {
        TrieNode* node = root;
        for (char c : word) {
            if (!node->children.count(c)) {
                node->children[c] = new TrieNode();
            }
            node = node->children[c];
        }
        node->wordsWithCost.push_back({word, cost});
    }
};
class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.size();
        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0; 
        Trie trie;
        for (size_t i = 0; i < words.size(); ++i) {
            trie.insert(words[i], costs[i]);
        }
        for (int i = 0; i < n; ++i) {
            if (dp[i] == INT_MAX) continue;
            TrieNode* node = trie.root;
            for (int j = i; j < n; ++j) {
                if (!node->children.count(target[j])) break;
                node = node->children[target[j]];
                for (const auto& [word, cost] : node->wordsWithCost) {
                    int len = word.size();
                    if (j + 1 >= i + len) {
                        dp[i + len] = min(dp[i + len], dp[i] + cost);
                    }
                }
            }
        }
        return dp[n] == INT_MAX ? -1 : dp[n];
    }
};
