class TrieNode:
    def __init__(self):
        self.children = {}
        self.cost = float('inf')

class Trie:
    def __init__(self):
        self.root = TrieNode()

    def insert(self, word, cost):
        node = self.root
        for char in word:
            if char not in node.children:
                node.children[char] = TrieNode()
            node = node.children[char]
        node.cost = min(node.cost, cost)

class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        n = len(target)
        trie = Trie()

        # Insert words and their costs into the Trie
        for word, cost in zip(words, costs):
            trie.insert(word, cost)

        dp = [float('inf')] * (n + 1)
        dp[0] = 0  # Cost to construct an empty string is 0

        for i in range(n):
            if dp[i] == float('inf'):
                continue
            node = trie.root
            for j in range(i, n):
                if target[j] not in node.children:
                    break
                node = node.children[target[j]]
                if node.cost != float('inf'):
                    dp[j + 1] = min(dp[j + 1], dp[i] + node.cost)

        return dp[n] if dp[n] != float('inf') else -1
