class Trie:
    def __init__(self):
        self.children={}
        self.cost=0
        self.end=0

class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        n,m=len(target),len(words)
        root=Trie()
        mp={}
        for i in range(m): mp[words[i]]=min(mp.get(words[i],sys.maxsize),costs[i])
        for word in mp:
            temp=root
            for c in word:
                if c not in temp.children: temp.children[c]=Trie()
                temp=temp.children[c]
            temp.end=1
            temp.cost=mp[word]
        @cache
        def dp(idx):
            if idx==n: return 0
            temp,ans=root,sys.maxsize
            for i in range(idx,n):
                c=target[i]
                if c not in temp.children: break
                temp=temp.children[c]
                if temp.end: ans=min(ans,dp(i+1)+temp.cost)
            return ans
        res=dp(0)
        return res if res<sys.maxsize else -1
