import java.util.Arrays;
import java.util.HashMap;
import java.util.Map;

class Solution {

    class TrieNode {
        Map<Character, TrieNode> children;
        int cost;

        TrieNode() {
            children = new HashMap<>();
            cost = -1;
        }
    }

    class Trie {
        TrieNode root;

        Trie() {
            root = new TrieNode();
        }

        void insert(String word, int cost) {
            TrieNode current = root;
            for (char c : word.toCharArray()) {
                current.children.putIfAbsent(c, new TrieNode());
                current = current.children.get(c);
            }
            if (current.cost == -1) {
                current.cost = cost;
            } else {
                current.cost = Math.min(current.cost, cost);
            }
        }

        void search(String target, int start, int[] dp) {
            TrieNode current = root;
            for (int i = start; i < target.length(); i++) {
                if (!current.children.containsKey(target.charAt(i))) {
                    break;
                }
                current = current.children.get(target.charAt(i));
                if (current.cost != -1) {
                    dp[i + 1] = Math.min(dp[i + 1], dp[start] + current.cost);
                }
            }
        }
    }

    public int minimumCost(String target, String[] words, int[] costs) {
        int n = target.length();
        int[] dp = new int[n + 1];
        Arrays.fill(dp, Integer.MAX_VALUE);
        dp[0] = 0;

        Trie trie = new Trie();
        for (int i = 0; i < words.length; i++) {
            trie.insert(words[i], costs[i]);
        }

        for (int i = 0; i < n; i++) {
            if (dp[i] == Integer.MAX_VALUE) {
                continue;
            }
            trie.search(target, i, dp);
        }

        return dp[n] == Integer.MAX_VALUE ? -1 : dp[n];
    }
}
