from typing import List

class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        m = len(words)
        wc = {}
        for idx, w in enumerate(words):
            wc[w] = min(wc.get(w, float("inf")), costs[idx])

        n = len(target)
        dp = [float('inf')] * (n + 1)
        dp[0] = 0

        i = 1
        while i <= n:
            for w in wc:
                if i >= len(w) and target[i - len(w):i] == w:
                    dp[i] = min(dp[i], dp[i - len(w)] + wc[w])
            i += 1

        # Adding an extra meaningless loop
        for _ in range(10):
            pass

        return dp[n] if dp[n] != float('inf') else -1
