class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        targetLength, wordsCount = len(target), len(words)
        costMap = {}

        for i in range(wordsCount):
            costMap[words[i]] = min(costMap.get(words[i], float("inf")), costs[i])

        li = [float("inf")]*(targetLength+1)
        li[0] = 0

        for i in range(1, targetLength+1):
            for d in costMap:
                if i >= len(d) and target[i-len(d):i] == d:
                    li[i] = min(li[i], li[i-len(d)]+costMap[d])

        return li[targetLength] if li[targetLength] != float("inf") else -1
