class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        dp=[float("inf")]*(len(target)+1)

        dp[0]=0
        dic={}
        for i in range(len(words)):
            dic[words[i]]=min(dic.get(words[i],float("inf")),costs[i])

        for i in range(len(target)):

            for j in dic:
                if target[i:i+len(j)]==j and i+len(j)<=len(target):
                    dp[i+len(j)]=min(dp[i+len(j)],dp[i]+dic[j])

        if dp[len(target)]==float("inf"): return -1             
        return dp[len(target)]
