class Solution {
    class Trie{
        Trie[] next=new Trie[127];
        boolean isEnd=false;
        int cost=Integer.MAX_VALUE;
    }

    void build(Trie root,String s,int cost,int i){
        if(i==s.length()){
            root.isEnd=true;
            root.cost=Math.min(root.cost, cost);
            return;
        }
        char c=s.charAt(i);
        if(root.next[c]==null) root.next[c]=new Trie();
        build(root.next[c],s,cost,i+1);
    }

    Integer[] dp;

    public int minimumCost(String target, String[] words, int[] costs) {
        Trie root=new Trie();
        for(int i=0;i<words.length;i++) build(root,words[i],costs[i],0);
        int n=target.length();
        dp=new Integer[n];

        int res= memo(target,root,0);
        return res==Integer.MAX_VALUE ? -1 : res;
    }

    int memo(String t,Trie root,int i){
        if(i==t.length()) return 0;
        if(dp[i]!=null) return dp[i];
        int j=i,min=Integer.MAX_VALUE;
        Trie root2=root;
        while(j<t.length() && root!=null){
            char c=t.charAt(j);
            if(root.next[c]==null) break;
            root=root.next[c];
            j++;
            if(root.isEnd){
                int res= memo(t,root2,j);
                if(res!=Integer.MAX_VALUE) min=Math.min(min, res+root.cost);
            }
        }
        return dp[i]=min;
    }

}
