class Solution {
public:
    struct node{
        node *ch[26];
        bool end;
        long long  val;

        node(){
            for(int i = 0; i < 26; i++) ch[i] = nullptr;
            end = false; val = INT_MAX;
        }
    };

    struct trie{
        node *r;
        vector<int> dp;

        trie(){
            r = new node();
        }

        void ins(string &s, int &v){
            node * c = r;
            for(auto x : s){
                if(c->ch[x - 'a'] == nullptr) c->ch[x - 'a'] = new node();
                c = c->ch[x - 'a'];
            }
            c->end = true;
            c->val = min(c->val, (long long)v);
            return;
        }


        long long f(string &s, int i){
            node *c = r;
            if(i == s.length()) return 0;
            if(dp[i] != -1) return dp[i];

            long long ans = INT_MAX;

            for(int j = i; j < s.length(); j++){
                c = c->ch[s[j] - 'a'];
                if(c == nullptr) break;
                if(c->end){
                    ans = min(ans, c->val + f(s, j + 1));
                }
            }

            return dp[i] = ans;
        }
    };

    int minimumCost(string s, vector<string>& w, vector<int>& c) {
        trie t;

        for(int i = 0; i < w.size(); i++){
            t.ins(w[i], c[i]);
        }

        t.dp.assign(s.length() + 1, -1);
        long long val = t.f(s, 0);

        if(val != INT_MAX) return val;

        return -1;
    }
};
