class Solution:
    def minimumCost(self, tg: str, words: List[str], costs: List[int]) -> int:
        n=len(tg)
        m=len(words)

        count={}
        for i in range(m):
            count[words[i]]=min(count.get(words[i],float("inf")),costs[i])

        dp=[float("inf")]*(n+1)
        dp[0]=0

        for i in range(1,n+1):
            for word in count:
                if i >=len(word) and tg[i-len(word):i]==word:
                    dp[i]=min(dp[i],dp[i-len(word)]+count[word])
        return dp[n] if dp[n]!=float("inf") else -1
