class Solution {
public:
    struct TrieNode {
        unordered_map<char, TrieNode*> ch;
        vector<pair<int, int>> wordEnds; 
    };

    class Trie {
    public:
        TrieNode* root;
        Trie() { root = new TrieNode(); }

        void insert(string& word, int i, int cs) {
            TrieNode* node = root;
            for (char c : word) {
                if (!node->ch.count(c))
                    node->ch[c] = new TrieNode();
                node = node->ch[c];
            }
            node->wordEnds.push_back({i, cs});
        }
    };

    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int m = target.size();
        vector<int> dp(m+ 1, INT_MAX);
        dp[0] = 0;

        Trie trie;
        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], i, costs[i]);
        }

        for (int i = 0; i < m; ++i) {
            if (dp[i] == INT_MAX) continue;

            TrieNode* node = trie.root;
            for (int j = i; j < m; ++j) {
                if (!node->ch.count(target[j])) break;
                node = node->ch[target[j]];
                for (auto& p : node->wordEnds) {
                    int wordIndex = p.first;
                    int wordCost = p.second;
                    int len = words[wordIndex].size();
                    if (i + len <= m) {
                        dp[i + len] = min(dp[i + len], dp[i] + wordCost);
                    }
                }
            }
        }

        return dp[m] == INT_MAX ? -1 : dp[m];
    }
};
