class A {
public:
    unordered_map<char, A*> b;
    vector<pair<int, int>> c;
};

class B {
public:
    A* d;

    B() {
        d = new A();
    }

    void e(string f, int g, int h) {
        A* i = d;
        for (char j : f) {
            if (!i->b.count(j)) {
                i->b[j] = new A();
            }
            i = i->b[j];
        }
        i->c.push_back({g, h});
    }
};

class Solution {
public:
    int solve(string t, vector<string>& u, vector<int>& v) {
        int w = t.size();
        vector<int> x(w + 1, INT_MAX);
        x[0] = 0;

        B y;
        for (int z = 0; z < u.size(); ++z) {
            y.e(u[z], z, v[z]);
        }

        for (int a = 0; a < w; ++a) {
            if (x[a] == INT_MAX) {
                continue;
            }

            A* b = y.d;
            for (int c = a; c < w; ++c) {
                if (!b->b.count(t[c])) {
                    break;
                }
                b = b->b[t[c]];
                for (auto& d : b->c) {
                    int e = u[d.first].size();
                    if (a + e <= w) {
                        x[a + e] = min(x[a + e], x[a] + d.second);
                    }
                }
            }
        }

        return x[w] == INT_MAX ? -1 : x[w];
    }

    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        return solve(target, words, costs);
    }
};
