class Solution {
    public int minimumCost(String target, String[] words, int[] costs) {
         int n = target.length();
        int[] dp = new int[n + 1];
        Arrays.fill(dp, Integer.MAX_VALUE);
        dp[0] = 0;

        // Use a map to store the minimum cost for each word
        Map<String, Integer> wordCostMap = new HashMap<>();
        for (int i = 0; i < words.length; i++) {
            wordCostMap.put(words[i], Math.min(wordCostMap.getOrDefault(words[i], Integer.MAX_VALUE), costs[i]));
        }

        // Iterate through the target string
        for (int i = 0; i < n; i++) {
            if (dp[i] == Integer.MAX_VALUE) continue;

            // Try to match all unique words starting from the current position
            for (Map.Entry<String, Integer> entry : wordCostMap.entrySet()) {
                String word = entry.getKey();
                int len = word.length();
                if (i + len <= n && target.startsWith(word, i)) {
                    dp[i + len] = Math.min(dp[i + len], dp[i] + entry.getValue());
                }
            }
        }

        return dp[n] == Integer.MAX_VALUE ? -1 : dp[n];
    }
}
