/**
 * @param {string} target
 * @param {string[]} words
 * @param {number[]} costs
 * @return {number}
 */
var minimumCost = function(target, words, costs) {
    const n = target.length
    const trie = {}
    for(let i = 0;i<words.length;i++){
        let node = trie
        const word = words[i]
        for(let j = 0;j<word.length;j++){
            const letter = word[j]
            if(node[letter] === undefined){node[letter] = {}}
            node = node[letter]
        }
        if(node.index !== undefined){
            if(costs[node.index] > costs[i]){node.index = i}
        }else{
            node.index = i
        }
    }
   const dp = new Array(target.length).fill(null)
   const dynamo = (index) => {
        if(index > n){return 1/0}
        if(index === n){return 0}
        if(dp[index] !== null){return dp[index]}
        let node = trie
        let best = 1/0
        for(let i = index;i<n;i++){
            const letter = target[i]
            if(node[letter] === undefined){break}
            node = node[letter]
            if(node.index !== undefined){
                const l = words[node.index].length
                const c = costs[node.index]
                best = Math.min(best,dynamo(index+l)+c)
            }
        }
        dp[index] = best
        return dp[index]
   }
   const res = dynamo(0)
   if(res === 1/0){return -1}
   return res  
};
