class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        n = len(target)
        word_cost_dict = {}
        for w, c in zip(words, costs):
            if not w in word_cost_dict or c < word_cost_dict[w]:
                word_cost_dict[w] = c

        @cache
        def dp(i):
            if i == n:
                return 0
            min_cost = float('inf')
            for word in word_cost_dict:
                if target[i:i+len(word)] == word:
                    min_cost = min(min_cost, word_cost_dict[word] + dp(i + len(word)))
            return min_cost

        res = dp(0)
        return res if res != float('inf') else -1
