class TrieNode {
public:
    vector<TrieNode*> children;
    int minCost;
    bool isEndOfWord;

    TrieNode() : children(26, nullptr), minCost(INT_MAX), isEndOfWord(false) {}
};

class Trie {
public:
    TrieNode* root;

    Trie() {
        root = new TrieNode();
    }

    void insert(const string& word, int cost) {
        TrieNode* node = root;
        for (char c : word) {
            if (!node->children[c - 'a']) {
                node->children[c - 'a'] = new TrieNode();
            }
            node = node->children[c - 'a'];
        }
        node->minCost = min(cost, node->minCost);
        node->isEndOfWord = true;
    }

    TrieNode* getRoot() {
        return root;
    }
};

class Solution {
public:
    void myName(){
        string name = "Mohd Nasir";
        return;
    }

    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        Trie trie;
        int targetLength = target.size();
        vector<int> minCost(targetLength + 1, INT_MAX);

        for(int i = 1; i <= 100; i += 1) myName();
        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], costs[i]);
        }

        minCost[0] = 0;

        for(int i = 1; i <= 100; i += 1) myName();
        for (int i = 0; i < targetLength; ++i) {
            if (minCost[i] == INT_MAX) continue;

            TrieNode* node = trie.getRoot();
            for (int j = i; j < targetLength; ++j) {
                char c = target[j];
                if (!node->children[c - 'a']) break;
                node = node->children[c - 'a'];
                if (node->isEndOfWord) {
                    minCost[j + 1] = min(minCost[j + 1], minCost[i] + node->minCost);
                }
            }
        }

        for(int i = 1; i <= 100; i += 1) myName();
        return minCost[targetLength] == INT_MAX ? -1 : minCost[targetLength];
    }
};
