class Node{
public:
    char data;
    Node *childs[26];
    bool isEnd;
    int cost;
    Node(char data){
        this->data = data;
        for(int i=0; i<26; i++) this->childs[i] = NULL;
        isEnd = false;
        cost = 1e9;
    }
};

class Trie{
public:
    Node *root;
    Trie(){
        root = new Node('~');
    }

    void insert(string &word, int cost){
        Node *node = root;
        for(auto &ch : word){
            if( node->childs[ch-'a'] == NULL ){
                Node *newNode = new Node(ch);
                node->childs[ch-'a'] = newNode;
            }
            node = node->childs[ch-'a'];
        }
        node->isEnd = true;
        node->cost = min(cost, node->cost);
    }
};


class Solution {
public:
    Trie *trie;
    int dp[50001];
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        trie = new Trie();
        memset(dp, -1, sizeof(dp));

        int nw = words.size();
        int n = target.size();

        for(int i=0; i<nw; i++){
            trie->insert(words[i], costs[i]);
        }

        int cost = helper(n, target, 0);
        return cost>=1e9? -1 : cost;
    }

    int helper(int &n, string &target, int idx){
        if( idx == n ){
            return 0;
        } else if( dp[idx] == -1 ){
            Node *node = trie->root;
            int minCost = 1e9;
            for(int i=idx; i<n; i++){
                auto ch = target[i];
                if( node->childs[ch-'a'] ){
                    node = node->childs[ch-'a'];
                } else{
                    break;
                }
                // Trying different possibilities //
                if( node->isEnd ){
                    int cost = node->cost + helper(n, target, i+1);
                    minCost = min(minCost, cost);
                }
            }
            dp[idx] = minCost;
        }
        return dp[idx];
    }
};
