class Solution {
    public int minimumCost(String target, String[] words, int[] costs) {
        var root = new Node();
        int m = words.length;
        for(int i = 0; i < m; ++i) {
            root.insert(words[i], costs[i]);
        }

        var chars = target.toCharArray();
        int n = chars.length;
        var dp = new int[n+1];
        Arrays.fill(dp, Integer.MAX_VALUE);
        dp[0] = 0;
        for(int i = 1; i <= n; ++i) {
            if (dp[i-1] < Integer.MAX_VALUE) {
                var node = root;
                for(int j = i; j <= n; ++j) {
                    int index = chars[j-1] - 'a';
                    var next = node.nexts[index];
                    if (next == null) {
                        break;
                    }
                    if (next.cost < Integer.MAX_VALUE) {
                        dp[j] = Math.min(dp[j], dp[i-1] + next.cost);
                    }
                    node = next;
                }
            }
        }
        return dp[n] < Integer.MAX_VALUE ? dp[n] : -1;
    }

    static class Node {

        Node[] nexts = new Node['z' - 'a' + 1];
        int cost = Integer.MAX_VALUE;

        void insert(String word, int cost) {
            var node = this;
            for(var ch : word.toCharArray()) {
                int index = ch - 'a';
                var next = node.nexts[index];
                if (next == null) {
                    next = new Node();
                    node.nexts[index] = next;
                }
                node = next;
            }
            node.cost = Math.min(node.cost, cost);
        }
    }
}
