class Solution {
public:
    class Trie{
    public:
        int contains, cost;
        Trie* child[26];
        Trie(){
            contains = 0, cost = 1e9+7;
            for(int i=0; i<26; i++){
                child[i] = nullptr;
            }
        }
        void insert(string s, int cost){
            int n = s.size();
            Trie* cur = this;
            cur->contains++;
            for(int i=0; i<n; i++){
                int bit = s[i]-'a';
                if(cur->child[bit]==nullptr) cur->child[bit] = new Trie();
                cur = cur->child[bit];
                cur->contains++;
            }
            cur->cost = min(cur->cost, cost);
        }
        int find(string s){
            int n = s.size();
            Trie* cur = this;
            for(int i=0; i<n; i++){
                int bit = s[i]-'a';
                if(cur->child[bit]==nullptr) return 1e9+7;
                cur = cur->child[bit];
            }
            return cur->cost;
        }
        Trie* get_head(){
            return this;
        } 
    };
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = words.size(), m = target.size(), INF = 1e9+7;
        Trie t = Trie();
        int i = 0;
        for(string& word: words){
            t.insert(word, costs[i]);
            i++;
        }
        vector<int> dp(m+1, INF);
        dp[0] = 0;
        for(int i=0; i<m; i++){
            Trie* cur = t.get_head();
            int j = 0;
            while(cur && i+j<m){
                int idx = target[i+j]-'a';
                if(cur->child[idx]!=nullptr){
                    cur = cur->child[idx];
                    j++;
                    dp[i+j] = min(dp[i+j], dp[i]+cur->cost);
                }
                else break;
            }
            // cout << i << ' ' << j << '\n';
        }
        if(dp[m]==INF) return -1;
        return dp[m];
    }
};
