class Trie:
    def __init__(self):
        self.t = {}

class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        T = Trie()
        for i, word in enumerate(words):
            cur = T.t
            for letter in word:
                if letter not in cur:
                    cur[letter] = {}
                cur = cur[letter]
            if "#" not in cur:
                cur["#"] = costs[i]
            else:
                cur["#"] = min(costs[i], cur["#"])
        #print(T.t)
        n = len(target)
        dp = [float('inf') for _ in range(n)]

        for i in range(n):
            cur = T.t
            j = i
            while j < n and target[j] in cur:
                cur = cur[target[j]]
                if "#" in cur:
                    if i > 0:
                        dp[j] = min(dp[j], dp[i - 1] + cur["#"])
                    else:
                        dp[j] = min(dp[j], cur["#"])


                j += 1

        return dp[-1] if dp[-1] != float('inf') else -1
