class TrieNode:
    def __init__(self):
        self.children = {}
        self.cost = float('inf')  # Store the minimum cost for this word

class Trie:
    def __init__(self):
        self.root = TrieNode()

    def insert(self, word, cost):
        node = self.root
        for char in word:
            if char not in node.children:
                node.children[char] = TrieNode()
            node = node.children[char]
        node.cost = min(node.cost, cost)  # Update the cost if it's a smaller value

    def search(self, s, start):
        node = self.root
        results = []
        for i in range(start, len(s)):
            if s[i] not in node.children:
                break
            node = node.children[s[i]]
            if node.cost < float('inf'):
                results.append((i + 1, node.cost))  # (end index, cost)
        return results

class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        n = len(target)
        dp = [float('inf')] * (n + 1)
        dp[0] = 0

        trie = Trie()
        for word, cost in zip(words, costs):
            trie.insert(word, cost)

        for i in range(n):
            if dp[i] == float('inf'):
                continue
            for end, cost in trie.search(target, i):
                dp[end] = min(dp[end], dp[i] + cost)

        return dp[n] if dp[n] != float('inf') else -1
