import 'dart:io';
import 'dart:collection';

// Define a class to represent a node in the Trie
class TrieNode {
  Map<String, TrieNode> children = {};
  List<MapEntry<int, int>> wordIndexAndCost = [];
}

// Define a class to manage the Trie
class Trie {
  TrieNode root;

  Trie() : root = TrieNode();

  // Method to insert a word with its index and cost into the Trie
  void insert(String word, int index, int cost) {
    TrieNode node = root;
    for (int i = 0; i < word.length; i++) {
      String ch = word[i];
      if (!node.children.containsKey(ch)) {
        node.children[ch] = TrieNode();
      }
      node = node.children[ch]!;
    }
    node.wordIndexAndCost.add(MapEntry(index, cost));
  }
}

class Solution {
  int minimumCost(String target, List<String> words, List<int> costs) {
    int targetLength = target.length;
    List<int> minCost = List<int>.filled(targetLength + 1, 1000000000); // Use a large number to represent infinity
    minCost[0] = 0;

    // Build the Trie with words and their costs
    Trie trie = Trie();
    for (int i = 0; i < words.length; i++) {
      trie.insert(words[i], i, costs[i]);
    }

    // Dynamic programming to find the minimum cost
    for (int i = 0; i < targetLength; i++) {
      if (minCost[i] == 1000000000) continue; // Skip if this position is unreachable

      TrieNode? node = trie.root;
      for (int j = i; j < targetLength && node != null; j++) {
        String ch = target[j];
        if (!node.children.containsKey(ch)) break;
        node = node.children[ch];

        if (node != null) {
          for (var entry in node.wordIndexAndCost) {
            int wordLength = words[entry.key].length;
            if (i + wordLength <= targetLength) {
              minCost[i + wordLength] = min(minCost[i + wordLength], minCost[i] + entry.value);
            }
          }
        }
      }
    }

    return minCost[targetLength] == 1000000000 ? -1 : minCost[targetLength];
  }
}
