class Solution {
    Integer[] dp;
    TrieNode root = new TrieNode();
    public int minimumCost(String target, String[] words, int[] costs) {
        int n = words.length;
        int m = target.length();
        dp = new Integer[m + 1];
        for (int i = 0; i < n; i++) {
            insert(words[i], costs[i]);
        }
        int res = dfs(0, target, root);
        return res == 1<<30 ? -1 : res;
    }
    public int dfs(int i, String s, TrieNode node) {
        if (i == s.length()) return 0;
        else if (dp[i] != null) return dp[i];
        int ret = 1<<30;
        TrieNode tmp = node;
        for (int j = i; j < s.length(); j++) {
            if (tmp.children[s.charAt(j) - 'a'] == null) {
                break;
            }
            tmp = tmp.children[s.charAt(j) - 'a'];
            if (tmp.isEnd) {
                // System.out.println(i + " " + j + " " + tmp.cost);
                ret = Math.min(ret, dfs(j + 1, s, root) + tmp.cost);
            }
        }
        // System.out.println(i + " " + ret);
        return dp[i] = ret;
    }
    public void insert(String str, int cost) {
        TrieNode node = root; 
        for (int i = 0; i < str.length(); i++){
            char ch = str.charAt(i);
            if(node.children[ch - 'a'] == null){
                node.children[ch - 'a'] = new TrieNode();
            }
            node = node.children[ch - 'a'];
        }
        node.isEnd = true;
        node.cost = Math.min(cost, node.cost);
    }
    class TrieNode {
        TrieNode children[];
        boolean isEnd;
        int cost;

        TrieNode(){
            this.children = new TrieNode[26];
            this.isEnd = false;
            this.cost = 1<<30;
        }
    }
}
