class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        vector<int> dp(target.size() + 1, INT_MAX);
        dp[0] = 0;

        vector<array<int, 27>> trie(2);
        for (int i = 0; i < words.size(); i++) {
            int curTrieIndex = 1;
            for (char c : words[i]) {
                if (trie[curTrieIndex][c - 'a'] == 0) {
                    trie[curTrieIndex][c - 'a'] = trie.size();
                    trie.push_back(array<int, 27>{});
                }
                curTrieIndex = trie[curTrieIndex][c - 'a'];
            }
            if (trie[curTrieIndex][26] == 0) {
                trie[curTrieIndex][26] = i + 1;
            } else if (costs[trie[curTrieIndex][26] - 1] > costs[i]) {
                trie[curTrieIndex][26] = i + 1;
            }
        }

        for (int i = 0; i < target.size(); i++) {
            if (dp[i] == INT_MAX) {
                continue;
            }
            int curTrieIndex = 1;
            int targetIndex = i;
            while (targetIndex < target.size() && curTrieIndex != 0) {
                curTrieIndex = trie[curTrieIndex][target[targetIndex] - 'a'];
                targetIndex++;
                if (trie[curTrieIndex][26] != 0) {
                    dp[targetIndex] = min(dp[targetIndex], dp[i] + costs[trie[curTrieIndex][26] - 1]);
                }
            }
        }

        if (dp.back() == INT_MAX) {
            return -1;
        }
        return dp.back();
    }
};
