class Solution {
public:
    struct TrieNode {
        unordered_map<char, TrieNode*> X;
        vector<pair<int, int>> Y; 
    };

    class Trie {
    public:
        TrieNode* root;
        Trie() { root = new TrieNode(); }

        void insert(string& word, int index, int cost) {
            TrieNode* node = root;
            for (char c : word) {
                if (!node->X.count(c))
                    node->X[c] = new TrieNode();
                node = node->X[c];
            }
            node->Y.push_back({index, cost});
        }
    };

    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.size();
        vector<int> ans(n + 1, INT_MAX);
        ans[0] = 0;

        Trie trie;
        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], i, costs[i]);
        }

        for (int i = 0; i < n; ++i) {
            if (ans[i] == INT_MAX) continue;

            TrieNode* node = trie.root;
            for (int j = i; j < n; ++j) {
                if (!node->X.count(target[j])) break;
                node = node->X[target[j]];
                for (auto& p : node->Y) {
                    int wordIndex = p.first;
                    int wordCost = p.second;
                    int len = words[wordIndex].size();
                    if (i + len <= n) {
                        ans[i + len] = min(ans[i + len], ans[i] + wordCost);
                    }
                }
            }
        }

        return ans[n] == INT_MAX ? -1 : ans[n];
    }
};
