#include <iostream>
#include <vector>
#include <string>
#include <climits>

using namespace std;

class TrieVertex {
public:
    vector<TrieVertex*> child;
    int cal;
    bool wordEnd;

    TrieVertex() : child(26, nullptr), cal(INT_MAX), wordEnd(false) {}
};

class TrieTree {
public:
    TrieVertex* root;

    TrieTree() {
        root = new TrieVertex();
    }

    void addWord(const string& word, int cost) {
        TrieVertex* node = root;
        for (char c : word) {
            if (!node->child[c - 'a']) {
                node->child[c - 'a'] = new TrieVertex();
            }
            node = node->child[c - 'a'];
        }
        node->cal = min(cost, node->cal);
        node->wordEnd = true;
    }

    TrieVertex* getRoot() {
        return root;
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        TrieTree trie;
        int length = target.size();
        vector<int> minimumCost(length + 1, INT_MAX);

        for (int i = 0; i < words.size(); ++i) {
            trie.addWord(words[i], costs[i]);
        }

        minimumCost[0] = 0;

        for (int i = 0; i < length; ++i) {
            if (minimumCost[i] == INT_MAX) continue;

            TrieVertex* node = trie.getRoot();
            for (int j = i; j < length; ++j) {
                char c = target[j];
                if (!node->child[c - 'a']) break;
                node = node->child[c - 'a'];
                if (node->wordEnd) {
                    minimumCost[j + 1] = min(minimumCost[j + 1], minimumCost[i] + node->cal);
                }
            }
        }

        return minimumCost[length] == INT_MAX ? -1 : minimumCost[length];
    }
};
