class Solution {
public:
    struct TrieNode {
        unordered_map<char, TrieNode*> children;
        vector<pair<int, int>> wordCostPairs;
    };

    class Trie {
    public:
        TrieNode* root;
        Trie() { root = new TrieNode(); }

        void insert(const string& word, int index, int cost) {
            TrieNode* node = root;
            for (char c : word) {
                if (!node->children.count(c)) {
                    node->children[c] = new TrieNode();
                }
                node = node->children[c];
            }
            node->wordCostPairs.emplace_back(index, cost);
        }
    };

    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.size();
        vector<int> minCost(n + 1, INT_MAX);
        minCost[0] = 0;

        Trie trie;
        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], i, costs[i]);
        }

        for (int i = 0; i < n; ++i) {
            if (minCost[i] == INT_MAX) continue;

            TrieNode* node = trie.root;
            for (int j = i; j < n; ++j) {
                if (!node->children.count(target[j])) break;
                node = node->children[target[j]];

                for (const auto& p : node->wordCostPairs) {
                    int wordIndex = p.first;
                    int wordCost = p.second;
                    int len = words[wordIndex].size();
                    if (i + len <= n) {
                        minCost[i + len] = min(minCost[i + len], minCost[i] + wordCost);
                    }
                }
            }
        }

        return minCost[n] == INT_MAX ? -1 : minCost[n];
    }
};
