class Solution {
    public int minimumCost(String target, String[] words, int[] costs) {
        Trie dic = new Trie();

        for (int i=0; i<words.length; i++) {
            Trie d = dic;

            for (char c : words[i].toCharArray()) {
                if (d.ch[c-'a'] == null) d.ch[c-'a'] = new Trie();
                d = d.ch[c-'a'];
            }
            d.end = true;
            d.cost = Math.min(d.cost, costs[i]);
        }

        int n = target.length();
        int[] dp = new int[n];
        Arrays.fill(dp, -2);

        return find(0, target, n, dic, dp);
    }

    int find(int i, String t, int n, Trie dic, int[] dp) {
        if (i == n) return 0;
        if (dp[i] != -2) return dp[i];

        Trie d = dic;
        int ans = -1;

        for (int j=i; j<n; j++) {
            int c = t.charAt(j) - 'a';

            if (d.ch[c] == null) break;
            d = d.ch[c];

            if (d.end) {
                int f = find(j+1, t, n, dic, dp);
                if (f == -1) continue;

                if (ans == -1) {
                    ans = d.cost + f;
                } else {
                    ans = Math.min(ans, d.cost + f);
                }
            }
        }

        return dp[i] = ans;
    }

    class Trie {
        Trie[] ch;
        boolean end;
        int cost;

        Trie() {
            ch = new Trie[26];
            end = false;
            cost = Integer.MAX_VALUE;
        }
    }
}
