class Solution:
    def minimumCost(self, tar: str, W: List[str], C: List[int]) -> int:
        T = lambda: defaultdict(T)
        trie = T()
        for w, c in zip(W, C):
            p = reduce(dict.__getitem__, reversed(w), trie)
            p[0] = min(p.get(0, inf), c)
        @cache
        def f(i):
            if i < 0: return 0
            p = trie
            res = inf
            while i >= 0 and tar[i] in p:
                p = p[tar[i]]
                if 0 in p: res = min(res, p[0] + f(i-1))
                i -= 1
            return res
        res = f(len(tar) - 1)
        return res if res != inf else -1
