constexpr int inf = (int)1e9;
class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& A) {

        array<int, 26> _def;    _def.fill(-1);
        vector<array<int, 26>> trie(1, _def);
        vector<int> cost(1, inf);
        for(int i = 0 ; i < words.size() ; ++i) {
            string S = words[i];
            int cur = 0;
            for(auto &c: S) {
                if(trie[cur][c - 'a'] == -1) {
                    trie[cur][c - 'a'] = trie.size();
                    trie.push_back(_def);
                    cost.push_back(inf);
                }
                cur = trie[cur][c - 'a'];
            }
            cost[cur] = min(cost[cur], A[i]);
        }
        int N = target.size();
        vector<int> dp(N + 1, inf);
        dp[0] = 0;
        for(int i = 0 ; i < N ; ++i) {
            int cur = 0;
            for(int j = i ; j < N ; ++j) {
                if(trie[cur][target[j] - 'a'] == -1) {
                    break;
                }
                cur = trie[cur][target[j] - 'a'];
                dp[j + 1] = min(dp[j + 1], dp[i] + cost[cur]);
            }
        }
        return (dp.back() == inf ? -1 : dp.back());
    }
};
