class Solution {
    class Trie {
        boolean isEnd;
        int c;
        Trie[] children;
        Trie(){
            this.isEnd = false;
            this.c = 0;
            this.children = new Trie[26];
        }
    }
    Trie root;
    public int minimumCost(String target, String[] words, int[] costs) {
        int n = target.length(), m = words.length;
        root = new Trie();
        for (int i = 0; i < m; ++i) {
            insert(words[i], costs[i], 0, root);
        }

        long[] dp = new long[n];
        Arrays.fill(dp, Integer.MAX_VALUE);

        for (int i = 0; i < n; ++i) {
            long rsf = 0;
            if (i != 0) rsf = dp[i - 1];
            search(root, target, i, n, dp, rsf, i);
        }

        // for (int i = 0; i < n; ++i) System.out.println(dp[i]);
        if (dp[n - 1] == Integer.MAX_VALUE) return -1;
        return (int)dp[n - 1];
    }

    private void search(Trie step, String target, int idx, int len, long[] dp, long rsf, int targetIdx) {
        if (idx == len) return;
        int me = target.charAt(idx) - 'a';
        if (step.children[me] != null) {
            if (step.children[me].isEnd) {
                int cost = step.children[me].c;
                dp[targetIdx] = Math.min(dp[targetIdx], rsf + cost);
            }
            search(step.children[me], target, idx + 1, len, dp, rsf, targetIdx + 1);
        }
    }

    private void insert(String word, int cost, int idx, Trie step) {
        if (word.length() == idx) {
            if (step.isEnd == false) {
                step.isEnd = true;
                step.c = cost;
            } else {
                step.c = Math.min(step.c, cost);
            }
        } else {
            int me = word.charAt(idx) - 'a';
            if (step.children[me] == null) step.children[me] = new Trie();
            insert(word, cost, idx + 1, step.children[me]);
        }
    }
}
