class Solution {
public:
    struct TrieNode 
    {
        unordered_map<char, TrieNode*> mp;
        //set
        vector<pair<int, int>> end; 
    };
    class Trie 
    {
    public:
        TrieNode* root;

        Trie() 
        { 
            root = new TrieNode(); 
        }



        void insert(string& word, int i, int co) 
        {
            TrieNode* node = root;
            for (char c : word) 
            {
                if (!node->mp.count(c))
                {
                    node->mp[c] = new TrieNode();
                }
                node = node->mp[c];
            }
            node->end.push_back({i, co});
        }
    };

    int help(string s, vector<string>& w, vector<int>& m)
    {
        int size = s.size();
        vector<int> v(size + 1, INT_MAX);
        v[0] = 0;

        Trie trie;
        for (int i = 0; i < w.size(); ++i) 
        {
            trie.insert(w[i], i, m[i]);
        }

        for (int i = 0; i < size; ++i) 
        {
            if (v[i] == INT_MAX) 
            {
                continue;
            }

            TrieNode* node = trie.root;
            for (int j = i; j < size; j++) 
            {
                if (!node->mp.count(s[j]))
                {
                    break;
                } 
                node = node->mp[s[j]];
                //st.insert();
                //for(auto &)
                for (auto& it : node->end) 
                {
                    int size1 = w[it.first].size();
                    if (i + size1 <= size) 
                    {
                        v[i + size1] = min(v[i + size1], v[i] + it.second);
                    }
                }
            }
        }
        if(v[size]==INT_MAX)
        {
            return -1;
        }
        return v[size];

    }

    int minimumCost(string target, vector<string>& words, vector<int>& costs) 
    {
        int ans=help(target,words,costs);
        //return res;

        return ans;

    }
};
